fs = require('fs');
var app = require('../../purplecheerio-wave');
var program = require('commander');

function main()
 {
 	//initialize();
 	program
		.version('1.0')
		.option('-s, --servicename <servicename>', 'servicename')
		.option('-c, --appserviceconfigfile <path>', 'appserviceconfigfile')
		.parse(process.argv);

	if (!process.argv.slice(2).length) {
	  program.outputHelp();
	  return;
	}

	if(program.servicename && program.appserviceconfigfile)
	{
		app.startservice(program.servicename, __dirname, program.appserviceconfigfile);
		return;
	}
	

 }

 main();



